import React, { useState } from 'react'
import LinkForm from './LinkForm';
import { toast } from "react-toastify";
import { db } from "../firebase";
import { collection, addDoc, doc, deleteDoc, onSnapshot, query } from "firebase/firestore";

import { useEffect } from 'react';

export const Links = () => {

  const [links, setLinks] = useState([])

  const addOrEditLink = async (linkObject) => {
    // await db.collection("links").doc().set(linkObject);

    await addDoc(collection(db, "links"), {linkObject});
    toast("Tarea Agregada"), {
      type: "success"
    }
  };

  const onDeleteLink = async (id) => {
    if(window.confirm("desea eliminar?")){
      //await db.collection("links").doc(id).delete();

      await deleteDoc (doc(db, "links", id));
      console.log("tarea eliminada")
    }
  };

  const getLinks = async () => {
    /*
    db.collection("links").onSnapshot((querySnapshot) => {
      const docs = [];
      querySnapshot.forEach(doc => {
        docs.push({ ...doc.data(), id: doc.id });
      });
      setLinks(docs);
    });
*/

const docs = query(collection(db, "links"));
const listenDocs = onSnapshot(docs, (querySnapshot) => {
  const docsResult = [];
  querySnapshot.forEach((doc) => {docsResult.push(doc.data())});
})
setLinks(listenDocs);
  };

  useEffect(() => {
    getLinks();
  }, []);
  return (
    <>
      <div className="col-md-4 p-2">
        <LinkForm addOrEditLink={addOrEditLink} />
      </div>

      <div className="col-md-8 p-2">
        {links.map(link =>
        //{return <h1> {link.name} </h1>}
        (
          <div className="card-mb-1" key={link.id}>
            <div className="card-body">
              <div className='d-flex justify-content-between'>
                <h4>{link.name}</h4>
                <i className='material_icons text-danger'
                  onClick={() => onDeleteLink(link.id)}>close</i>
              </div>
              <p>{link.descripcion}</p>
              <a href={link.url} target="_blank" rel="noopener noreferrer">Ir al Sitio Web</a>
            </div>
          </div>
        )
        )}
      </div>
    </>
  )
};


export default Links;
