import React, { useState } from 'react'
import LinkForm from './LinkForm';
import { toast } from "react-toastify";
import { db } from "../firebase";
import { collection, addDoc, doc, deleteDoc, onSnapshot, query } from "firebase/firestore";
import { useEffect } from 'react';


export const Links = () => {

  const [links, setLinks] = useState([])

  const addOrEditLink = async (linkObject) => {
    // await db.collection("links").doc().set(linkObject);

    await addDoc(collection(db, "links"), { linkObject });
    toast("Tarea Agregada")
  };

  const onDeleteLink = async (id) => {
    if (window.confirm("desea eliminar?")) {
      //await db.collection("links").doc(id).delete();

      await deleteDoc(doc(db, "links", id));
      console.log("tarea eliminada")
    }
  };

  const getLinks = async () => {
    /*
    db.collection("links").onSnapshot((querySnapshot) => {
      const docs = [];
      querySnapshot.forEach(doc => {
        docs.push({ ...doc.data(), id: doc.id });
      });
      setLinks(docs);
    });
*/

    const docs =
      query(collection(db, "links"));
    const listenDocs = onSnapshot(docs, (querySnapshot) => {
      const docsResult = [];
      querySnapshot.forEach((doc) => {
        docsResult.push(doc.data(), doc.id)
      });
    })
    setLinks(listenDocs);
  };

  useEffect(() => {
    getLinks();
  }, []);
  return (
    <>
      <div className="col-md-4 p-2">
        <LinkForm addOrEditLink={addOrEditLink} />
      </div>

     
    </>
  )
};


export default Links;
