import React, { useState } from 'react'
import LinkForm from './LinkForm';
import { toast } from "react-toastify";
import { db } from "../firebase";
import { useEffect } from 'react';


export const Tareas = () => {

  const [tareas, setTareas] = useState([]);
  const [currentId, setCurrentId] = useState('')

  const addOrEditTarea = async (tareaObject) => {
    try {
      if (currentId === '') {
        await db.collection("links").doc().set(linkObject);

        //await addDoc(collection(db, "links"), { linkObject });
        toast("Tarea Agregada", {
          type: "success",
          autoClose: 1000,
          position: "top-center"
        });
      } else {
        await db.collection('links').doc(currentId).update(linkObject);
        toast("Tarea Actualizada", {
          type: "info",
          autoClose: 1000,
          position: "top-center"
        });
        setCurrentId('');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const onDeleteLink = async (id) => {
    if (window.confirm("desea eliminar?")) {
      await db.collection("links").doc(id).delete();

      //await deleteDoc(doc(db, "links", id));
      toast("tarea eliminada", {
        type: "error",
        autoClose: 1000,
        position: "top-center"
      })
    }
  };

  const getLinks = async () => {
    db.collection("links").onSnapshot((querySnapshot) => {
      const docs = [];
      querySnapshot.forEach(doc => {
        docs.push({ ...doc.data(), id: doc.id });
      });
      setLinks(docs);
    });
  }


  useEffect(() => {
    getLinks();
  }, []);


  return (
    <>
      <div className="col-md-8 p-2">
        <LinkForm {...{ addOrEditLink, currentId, links }} />
      </div>
      <div className="col-md-8 p-2">
        {links.map(link =>
        //{return <h1> {link.name} </h1>}

        (
          <div className="card mb-1" key={link.id}>
            <div className="card-body">
              <div className='d-flex justify-content-between'>
                <h5>{link.url}</h5>
                <div>
                  <button className='btn'>
                    <i className='material-icons text-danger'
                      onClick={() => onDeleteLink(link.id)}>delete</i>
                  </button>
                  <button className='btn'>
                    <i className='material-icons'
                      onClick={() => setCurrentId(link.id)}>edit</i>
                  </button>
                </div>
              </div>
              <h5 className='text-info'>{link.name}</h5>
              <p className='d-flex p-4'>{link.descripcion}</p>
            </div>
          </div>
        )

        )}
      </div>
    </>
  )
};


export default Links;
